    re.empty?.wont_be_nil
    pre.each { |d| d.must_match /[a-zA-Z]/ }
    re.each { |d| d.must_match /^[0-9.]+$/ }
  end

  it 'has no releases' do
    deps = Nexus::Dependencies.new( 'pre', [{:name=>"pre", :number=>"0.1.0.beta", :platform=>"ruby", :dependencies=>[]}] )
